    int turnCount = 0;
    string[] game = new string[9];
    string O = "O";
    string X = "X";
    bool botgame = true;
    bool botturn = false;
    string playerOWins = string.Empty;
    string playerXWins = string.Empty;
    bool xWon = false;
    bool oWon = false;
    bool gameover = false;

    protected override async Task OnInitializedAsync()
    {
        await Task.Run(NewGame);

    }

    protected override void OnAfterRender(bool firstRender)
    {
        if (botturn)
        {
            BotPlay();
            StateHasChanged();
        }
    }


    void NewGame()
    {
        turnCount = 0;
        gameover = false;
        playerOWins = string.Empty;
        playerXWins = string.Empty;
        oWon = false;
        xWon = false;
        for (var i = 0; i < game.Length; i++)
            game[i] = string.Empty;
    }

    void BotSetup()
    {
        botgame = true;
        botturn = false;
        gameover = false;
        NewGame();
    }

    void HumanSetup()
    {
        botgame = false;
        botturn = false;
        NewGame();
    }

    void GameClick(int i)
    {
        if (gameover)
            return;

        if (turnCount.isEven())
        {

            game[i] = X;

            if (game.XWins())
            {
                playerXWins = "Player X has won the game!";
                xWon = true;
                gameover = true;
                return;
            }

            if (botgame)
            {
                botturn = true;
            }
        }
        else
        {
            game[i] = O;
        }
        turnCount++;
    }

    protected void BotPlay()
    {
        if (!botgame)
            return;

        if (gameover)
            return;

        var rng = new Random();
        Thread.Sleep(rng.Next(100, 3000));


        // 0 1 2
        // 3 4 5
        // 6 7 8
        bool shouldSetMiddle = string.IsNullOrEmpty(game[4]) || game.ShouldSetMiddle();
        if (shouldSetMiddle)
        {
            game[4] = O;
        }
        else if (game.ShouldSetUpperLeft())
            game[0] = O;
        else if (game.ShouldSetUpperMiddle())
            game[1] = O;
        else if (game.ShouldSetUpperRight())
            game[2] = O;
        else if (game.ShouldSetLeftMiddle())
            game[3] = O;
        else if (game.ShouldSetRightMiddle())
            game[5] = O;
        else if (game.ShouldSetLowerLeft())
            game[6] = O;
        else if (game.ShouldSetLowerMiddle())
            game[7] = O;
        else if (game.ShouldSetLowerRight())
            game[8] = O;
        else
        {

            var randomEmpty = Array.IndexOf(game, string.Empty, rng.Next(0, 8));
            game[randomEmpty] = O;

        }

        turnCount++;
        botturn = false;

        if (game.OWins())
        {
            playerOWins = "Player O has won!";
            oWon = true;
            gameover = true;
            return;
        }
    }

