    int turnCount;
    string[] game;
    string O;
    string X;
    bool botgame;
    bool botturn;
    string playerOWins;
    string playerXWins;
    string playerCatGame;
    bool xWon;
    bool oWon;
    bool catGame;
    bool gameover;

    protected override async Task OnInitializedAsync()
    {
        await Task.Run(NewGame);

    }

    protected override void OnAfterRender(bool firstRender)
    {
        if (botturn)
        {
            BotPlay();
            StateHasChanged();
        }
    }


    void NewGame()
    {
        
        turnCount = 0;
        game = new string[9];
        O = "O";
        X = "X";
        botgame = true;
        botturn = false;
        playerOWins = string.Empty;
        playerXWins = string.Empty;
        playerCatGame = string.Empty;
        oWon = false;
        xWon = false;
        catGame = false;
        gameover = false;
        for (var i = 0; i < game.Length; i++)
            game[i] = string.Empty;
    }

    void BotSetup()
    {
        botgame = true;
        botturn = false;
        gameover = false;
        NewGame();
    }

    void HumanSetup()
    {
        botgame = false;
        botturn = false;
        NewGame();
    }

    void GameClick(int i)
    {
        if (gameover)
            return;

        if (!string.IsNullOrEmpty(game[i]))
            return;

        if (turnCount.isEven())
        {
            game[i] = X;
            if (PlayerXWin())
                return;

            if (PlayerCatGame())
                return;

            if (botgame)
                botturn = true;           
        }
        else
        {
            game[i] = O;
            if (PlayerOWin())
                return;

            if (PlayerCatGame())
                return;

        }
        turnCount++;
    }

    protected void BotPlay()
    {
        if (!botgame)
            return;

        if (gameover)
            return;

        var rng = new Random();
        Thread.Sleep(rng.Next(100, 3000));


        // 0 1 2
        // 3 4 5
        // 6 7 8
        bool shouldSetMiddle = string.IsNullOrEmpty(game[4]) || game.ShouldSetMiddle();
        if (shouldSetMiddle)
        {
            game[4] = O;
        }
        else if (game.ShouldSetUpperLeft())
            game[0] = O;
        else if (game.ShouldSetUpperMiddle())
            game[1] = O;
        else if (game.ShouldSetUpperRight())
            game[2] = O;
        else if (game.ShouldSetLeftMiddle())
            game[3] = O;
        else if (game.ShouldSetRightMiddle())
            game[5] = O;
        else if (game.ShouldSetLowerLeft())
            game[6] = O;
        else if (game.ShouldSetLowerMiddle())
            game[7] = O;
        else if (game.ShouldSetLowerRight())
            game[8] = O;
        else
        {

            var randomEmpty = Array.IndexOf(game, string.Empty, rng.Next(0, 8));
            game[randomEmpty] = O;

        }

        turnCount++;
        botturn = false;

        if (PlayerOWin())
            return;

        if (PlayerCatGame())
            return;
    }

    private bool PlayerOWin()
    {
        if (game.OWins())
        {
            playerOWins = "Player O has won!";
            oWon = true;
            gameover = true;
            return true;
        }
        return false;
    }

    private bool PlayerXWin()
    {
        if (game.XWins())
        {
            playerXWins = "Player X has won the game!";
            xWon = true;
            gameover = true;
            return true;
        }
        return false;
    }

    private bool PlayerCatGame()
    {
        if (game.CatGame())
        {
            playerCatGame = "Cat game, everyone wins!";
            catGame = true;
            gameover = true;
            return true;
        }
        return false;
    }

